    <script>
        function initAutocomplete() {
            const input = document.getElementById("pac-input");
            const autocomplete = new google.maps.places.Autocomplete(input);

            autocomplete.addListener("place_changed", () => {
                const place = autocomplete.getPlace();

                if (!place.geometry || !place.geometry.location) {
                    console.error("No details available for input: '" + place.name + "'");
                    return;
                }

                const lat = place.geometry.location.lat();
                const lng = place.geometry.location.lng();
                const city = getComponentValue(place.address_components, "locality");
                const state = getComponentValue(place.address_components, "administrative_area_level_1");
                const zipcode = getComponentValue(place.address_components, "postal_code");

                console.log("Latitude:", lat);
                console.log("Longitude:", lng);
                console.log("City:", city);
                console.log("State:", state);
                console.log("ZIP Code:", zipcode);

                var csrfToken = "{{ csrf_token() }}";
                let data = {'lat':lat,'long':lng,'city':city,'state':state,'zipcode':zipcode,'search':input.value};

                $('.spinner-loader').show();
                $('#map').hide();
                $('#tab2').hide();
                $.ajax({
                    type: "POST",
                    url: "{{ route('searchIndex') }}",
                    data: data,
                    headers: {
                        'X-CSRF-TOKEN': csrfToken
                    },
                    dataType: 'json',
                    success: function(response) {
                        $('#map').show();
                        $('#tab2').show();
                        $('.spinner-loader').hide();
                        if (response.properties) {
                            var propertiesData = response.properties;
                            updateMapWithData(propertiesData);
                        }
                        $.ajax({
                            type: "POST",
                            url: "{{ route('searchIndex') }}",
                            data: data,
                            headers: {
                                'X-CSRF-TOKEN': csrfToken
                            },
                            success: function(response) {
                                $('#map').show();
                                $('#tab2').show();
                                $("#tab2").html(response);
                                $('.spinner-loader').hide();
                            },
                            error: function(error) {
                                $('#map').show();
                                $('#tab2').show();
                                $('.spinner-loader').hide();
                                console.error("Error:", error);
                            }
                        });
                    },
                    error: function(error) {
                        $('#map').show();
                        $('#tab2').show();
                        $('.spinner-loader').hide();
                        console.error("Error:", error);
                    }
                });


            });
        }

        function getComponentValue(components, type) {
            for (let i = 0; i < components.length; i++) {
                if (components[i].types.includes(type)) {
                    return components[i].long_name;
                }
            }
            return "";
        }
        function initMapAndAutocomplete() {
            initMap();
            initAutocomplete();
        }
    </script>
