    <script>
        function checkNextRadio(clickedElement) {
            // Get all children of the clicked element
            var children = clickedElement.children;

            // Iterate through children to find the input with the specified name
            for (var i = 0; i < children.length; i++) {
                var child = children[i];
                if (child.tagName === 'INPUT' && child.name === 'property_type') {
                    // Check the radio button
                    child.checked = true;
                    break; // Exit the loop once found
                }
            }
        }

        window.onload = $('.spinner-loader').hide();

        function submitForm() {
            $('.spinner-loader').show();
            $('#map').hide();
            $('#tab2').hide();
            var csrfToken = "{{ csrf_token() }}";
            var formData = $("#filter_form").serialize();
            $.ajax({
                type: "POST",
                url: "{{ route('filterIndex') }}",
                data: formData,
                headers: {
                    'X-CSRF-TOKEN': csrfToken
                },
                success: function(response) {
                    $('#map').show();
                    $('#tab2').show();
                    $("#tab2").html(response);
                    $('.spinner-loader').hide();
                },
                error: function(error) {
                    $('#map').show();
                    $('#tab2').show();
                    $('.spinner-loader').hide();
                    console.error("Error:", error);
                }
            });
            $.ajax({
                type: "POST",
                url: "{{ route('filterIndex') }}",
                data: formData,
                headers: {
                    'X-CSRF-TOKEN': csrfToken
                },
                dataType: 'json',
                success: function(response) {
                    if (response.properties) {
                        var propertiesData = response.properties;
                        updateMapWithData(propertiesData);
                    }
                },
                error: function(error) {
                    console.error("Error:", error);
                }
            });
        }

        $("#filter_form").submit(function(event) {
            event.preventDefault();
            submitForm();
        });
    </script>
